// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

/// @title Decentralized Voting System with Delegation
/// @dev This contract allows users to vote directly or delegate their votes.
contract Voting {
    
    /// @notice Represents a voter with voting rights and delegation status
    struct Voter {
        uint weight; // Voting weight (increased if votes are delegated)
        bool voted; // Indicates whether the voter has cast a vote
        address delegate; // Address of the delegate
        uint vote; // Index of the voted proposal
    }

    /// @notice Represents a proposal with a name and vote count
    struct Proposal {
        bytes32 name; // Proposal name (stored as bytes32 for efficiency)
        uint voteCount; // Accumulated votes for the proposal
    }

    address public chairperson; // Address of the contract creator (admin)
    mapping(address => Voter) public voters; // Mapping of voter addresses to voter details
    Proposal[] public proposals; // Array of proposals

    /// @notice Deploys the contract and initializes proposals
    /// @param proposalNames List of proposal names
    constructor(bytes32[] memory proposalNames) {
        chairperson = msg.sender;
        voters[chairperson].weight = 1; // Chairperson has initial voting rights

        for (uint i = 0; i < proposalNames.length; i++) {
            proposals.push(Proposal({name: proposalNames[i], voteCount: 0}));
        }
    }

    /// @notice Grants voting rights to an address
    /// @dev Can only be called by the chairperson
    /// @param voter Address of the voter
    function giveRightToVote(address voter) external {
        require(msg.sender == chairperson, "Only chairperson can grant voting rights.");
        require(!voters[voter].voted, "Voter has already voted.");
        require(voters[voter].weight == 0, "Voter already has rights.");
        
        voters[voter].weight = 1;
    }

    /// @notice Delegates the sender's vote to another voter
    /// @param to Address of the delegate
    function delegate(address to) external {
        Voter storage sender = voters[msg.sender];
        require(sender.weight != 0, "No voting rights.");
        require(!sender.voted, "Already voted.");
        require(to != msg.sender, "Self-delegation is not allowed.");

        while (voters[to].delegate != address(0)) {
            to = voters[to].delegate;
            require(to != msg.sender, "Delegation cycle detected.");
        }

        Voter storage delegate_ = voters[to];
        require(delegate_.weight >= 1, "Delegate has no voting rights.");

        sender.voted = true;
        sender.delegate = to;

        if (delegate_.voted) {
            proposals[delegate_.vote].voteCount += sender.weight;
        } else {
            delegate_.weight += sender.weight;
        }
    }

    /// @notice Casts a vote for a proposal
    /// @param proposal Index of the chosen proposal
    function vote(uint proposal) external {
        Voter storage sender = voters[msg.sender];
        require(sender.weight != 0, "No voting rights.");
        require(!sender.voted, "Already voted.");
        require(proposal < proposals.length, "Invalid proposal index.");

        sender.voted = true;
        sender.vote = proposal;
        proposals[proposal].voteCount += sender.weight;
    }

    /// @notice Determines the winning proposal based on vote count
    /// @return winningProposal_ Index of the proposal with the highest votes
    function winningProposal() public view returns (uint winningProposal_) {
        uint winningVoteCount = 0;
        for (uint p = 0; p < proposals.length; p++) {
            if (proposals[p].voteCount > winningVoteCount) {
                winningVoteCount = proposals[p].voteCount;
                winningProposal_ = p;
            }
        }
    }

    /// @notice Returns the name of the winning proposal
    /// @return winnerName_ Name of the winning proposal
    function winnerName() external view returns (bytes32 winnerName_) {
        winnerName_ = proposals[winningProposal()].name;
    }
}

